    function findTextWithHtml(obj) {
      if (!obj || typeof obj !== 'object') return null;

      if (typeof obj === 'string' && obj.includes('<html')) {
        return obj;
      }

      if (Array.isArray(obj)) {
        for (const item of obj) {
          const found = findTextWithHtml(item);
          if (found) return found;
        }
      } else {
        for (const key in obj) {
          const found = findTextWithHtml(obj[key]);
          if (found) return found;
        }
      }
      return null;
    }

    htmlContent = findTextWithHtml(inputData);
